mapper.select:
select * from GEN2_CONFIG

      and id = #{id} 
      and author like #{author}
      and version like #{version}
      and camel_case = #{camel_case}
      and base_package like #{base_package}
      and module_name like #{module_name}
      and database_id like #{database_id}
      and status = #{status}
      and "desc" = #{desc}
      and update_version = #{update_version}

order by id desc

SELECT * FROM GEN2_CONFIG

     and id = #{id}
     and author like #{author}
     and version like #{version}
     and camel_case = #{camel_case}
     and base_package like #{base_package}
     and module_name like #{module_name}
     and database_id like #{database_id}
     and status = #{status}
     and `desc` = #{desc}
     and update_version = #{update_version}

ORDER BY ID DESC

SELECT T1.* FROM ( SELECT T.*,ROWNUM AS R FROM GEN2_CONFIG T

     and id = #{id}
     and author like #{author}
     and version like #{version}
     and camel_case = #{camel_case}
     and base_package like #{base_package}
     and module_name like #{module_name}
     and database_id like #{database_id}
     and status = #{status}
     and `desc` = #{desc}
     and update_version = #{update_version}

ORDER BY ID DESC
) T1

    WHERE T1.R > #{page_size}*#{page_no}
    AND   T1.R <= #{page_size}*(#{page_no}+1)

mapper.update:
update GEN2_CONFIG
author = #{author}, version = #{version}, camel_case = #{camel_case}, base_package = #{base_package}, module_name = #{module_name}, database_id = #{database_id}, status = #{status}, "desc" = #{desc}, update_version = #{update_version},
and id = #{id}

update GEN2_CONFIG
author = #{author}, version = #{version}, camel_case = #{camel_case}, base_package = #{base_package}, module_name = #{module_name}, database_id = #{database_id}, status = #{status}, `desc` = #{desc}, update_version = #{update_version},
and id = #{id}

update GEN2_CONFIG
author = #{author}, version = #{version}, camel_case = #{camel_case}, base_package = #{base_package}, module_name = #{module_name}, database_id = #{database_id}, STATUS = #{status}, "DESC" = #{desc}, update_version = #{update_version},
and id = #{id}

update GEN2_CONFIG
set status = #{status}
where id != #{id}
